class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n=nums.size();
long long ct=0;
map<long long,long long>pr;
for(int i=0;i<n;i++){
    map<long long,long long>cr;
    if(nums[i]==k)ct++;
    cr[nums[i]]=1;
    for(auto &[value,frq]:pr){
        long long p1=value;
        long long q1=nums[i];
        long long res=p1 & q1;
        if(res==k)ct=ct+frq;
        cr[res]=cr[res]+frq;
    }
    pr=cr;
}
return ct;
    }
};
