#define ll long long
class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n=nums.size();
        ll ans=0;
        map<ll,ll>m1;
        for(int i=0;i<n;i++)
        {
            if(nums[i]==k)
                ans++;
        }
        for(int i=0;i<n;i++)
        {
            map<ll,ll>m2;
            m2[nums[i]]=1;
            for(auto& it:m1)
            {
                if((it.first&nums[i])==k)
                {
                    ans+=it.second;
                }
                m2[(it.first&nums[i])]+=it.second;
            }
            m1=m2;
        }
        return ans;
    }
};
