class Solution
{
public:
    long long countSubarrays(vector<int> &nums, int k)
    {
        int n = nums.size();
        long long cnt = 0;

        map<long long, long long> dp;

        for (int i = 0; i < n; ++i)
        {
            map<long long, long long> ndp;

            cnt += (nums[i] == k);
            ndp[nums[i]] = 1;

            for (auto &[x, y] : dp)
            {
                long long newAns = x & nums[i];
                cnt += (y * (newAns == k));
                ndp[newAns] += y;
            }

            dp = ndp;
        }

        return cnt;
    }
};
