#define ll long long

class Solution {
public:
    long long countSubarrays(vector<int>& array, int target) {
        int n = array.size();
        long long count = 0;
        map<long long, long long> prevResults;

        for (int i = 0; i < n; ++i) {
            map<long long, long long> currResults;

            if (array[i] == target) {
                ++count;
            }

            currResults[array[i]] = 1;

            for (auto& [val, freq] : prevResults) {
                long long newAnd = val & array[i];
                if (newAnd == target) {
                    count += freq;
                }
                currResults[newAnd] += freq;
            }

            prevResults = currResults;
        }

        return count;
    }
};
