#define ll              long long
class Solution {
public:

    long long countSubarrays(vector<int>& nums, int k) {
    int n = nums.size();
    long long ans = 0;
    unordered_map<long long, long long> mp;

    for (int i = 0; i < n; ++i) {
        unordered_map<long long, long long> step; 

        if (nums[i] == k) {
            ++ans;
        }

        step[nums[i]] = 1;

        for (auto& [fr, sc] : mp) {
            long long val = fr & nums[i];

            if (val == k) {
                ans += sc;
            }

            step[val] += sc;
        }

        mp = step;
    }

    return ans;
}

};
