class Solution {
public:
    long long countSubarrays(vector<int>& nums, int target) {
        const int n = nums.size();
        long long total = 0;
        unordered_map<long long, long long> dp;

        for (int i = 0; i < n; ++i) {
            unordered_map<long long, long long> dp2; 

            if (nums[i] == target) {
                ++total;
            }

            dp2[nums[i]] = 1;

            for (auto& [val, freq] : dp) {
                long long n_and = val & nums[i];

                if (n_and == target) {
                    total += freq;
                }

                dp2[n_and] += freq;
            }

            dp = dp2;
        }

        return total;
    }
};
