class Solution {
    public long countSubarrays(int[] nums, int k) {
        long res = 0;
        var level = new HashMap<Long, Long>();
        for (long i : nums) {
            var next = new HashMap<Long, Long>(Map.of(i, 1L));

            for (var e : level.entrySet()) {
                var v = i & e.getKey();
                if (v == k) {
                    res += e.getValue();
                }
                next.merge(v, e.getValue(), Long::sum);
            }
            level = next;
            if (i == k) {
                res++;
            }
        }
        return res;
    }
}
