class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long ans=0;
        int n = nums.size();
        vector<int> cnt(n,1);
        unordered_map<long long,long long> m;
        for(int i=0;i<n;i++){
            vector<pair<long,long>> a;
            for(auto [x,y] : m){
                if((nums[i]&x)==k) ans+=y;
                a.push_back({(nums[i]&x),y});
            }
            if(nums[i]==k) ans++;
            m.clear();
            a.push_back({nums[i],1});
            for( auto [x,y] : a){
                m[x]+=y;
            }
        }
        return ans;
    }
};
