using ll = long long;
class Solution
{
public:
    ll countSubarrays(vector<int> &nums, int k)
    {
        ll n = size(nums), ans = 0;  map<ll, ll> mp;
        for (int i : nums)
        {
            map<ll, ll> mp2; ans += i == k, mp2[i] = 1;
            for (auto &[e, f] : mp)
            {
                (e & i) == k ? ans += f : ans += 0;
                mp2[e & i] += f;
            }
            mp = mp2;
        }
        return ans;
    }
};
