typedef long long ll;
class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        ll ans = 0;
        vector<vector<int>> loc(n + 1, vector<int>(35, n));

        for(int i = n - 1; i >= 0; i--) {
            for(int j = 0; j < 31; j++) {
                if(nums[i] & (1 << j)) loc[i][j] = loc[i + 1][j];
                else loc[i][j] = i;
            }
        }
        vector<int> is(35);
        for(int j = 0; j < 31; j++) if(k & (1 << j)) is[j] = 1;
        for(int i = 0; i < n; i++) {
            int l = i, r = n - 1;
            for(int j = 0; j < 31; j++) {
                if(is[j]) r = min(loc[i][j] - 1, r);
                else l = max(loc[i][j], l);
            }
            if(r - l + 1 >= 0) ans += r - l + 1;
        }
        return ans;
    }
};
