class Solution {
public:
    long long countSubarrays(vector<int>& a, int k) {
        long long ans = 0;
        int i = 1;

        vector<long long> h(30);

        for (auto x: a) {
            vector<long long> g = h;
            long long mn = i, mx = 0;
            for (int i = 0; i < 30; i++) {
                if (x & (1 << i)) g[i]++;
                else g[i] = 0;

                if (k & (1 << i)) mn = min(mn, g[i]);
                else mx = max(mx, g[i]); 
            }

            h = g;
            if (mx <= mn) ans += (mn - mx);
            i++;
        }

        return ans;

    }
};
