class Solution {
public:
    long long countSubarrays(vector<int>& array, int target) {
        int size = array.size();
        long long total = 0;
        unordered_map<long long, long long> currentResults; 
        for (int index = 0; index < size; ++index) {
            unordered_map<long long, long long> newResults;
            if (array[index] == target) {
                ++total;
            }
            newResults[array[index]] = 1;
            for (auto& [value, frequency] : currentResults) {
                long long newAndResult = value & array[index];
                if (newAndResult == target) {
                    total += frequency;
                }
                newResults[newAndResult] += frequency;
            }

            currentResults = newResults;
        }
        return total;
    }
};
