using System;
using System.Collections.Generic;

public class Solution {
    public long CountSubarrays(int[] nums, int k) {
        int n = nums.Length;
        long count = 0;

        Dictionary<long, long> prevResults = new Dictionary<long, long>();

        for (int i = 0; i < n; ++i) {
            Dictionary<long, long> currResults = new Dictionary<long, long>(); 

            if (nums[i] == k) {
                ++count;
            }
            currResults[nums[i]] = 1;

            foreach (var kvp in prevResults) {
                long newAndResult = kvp.Key & nums[i];
                if (newAndResult == k) {
                    count += kvp.Value;
                }
                if (currResults.ContainsKey(newAndResult)) {
                    currResults[newAndResult] += kvp.Value;
                }
                else {
                    currResults.Add(newAndResult, kvp.Value);
                }
            }

            prevResults = new Dictionary<long, long>(currResults);
        }

        return count;
    }
}
