class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n=nums.size();
        vector<map<long long,long long>>dp(n);
        long long c=0;
        for(long long i=0;i<n;i++)
        {
            if(nums[i]==k)
            {
                c++;
            }
            dp[i][nums[i]]++;
            if(i>0)
            {
                for(auto &[z,j]:dp[i-1])
                {
                    if((z&nums[i])==k)
                    {
                        c=c+j;
                    }
                    dp[i][(z&nums[i])]+=j;
                }
            }
        }
        return c;
    }
};
