class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        res = 0
        seen = Counter()
        seen[0] = 0
        for num in nums:
            c = Counter()
            for key, freq in seen.items():
                c[key&num] += freq
            c[num] += 1
            for key, v in c.items():
                if key == k:
                    res += v
            seen = c
        return res
