class Solution {
public:
    long long countSubarrays(vector<int>& A, int k) {
        int N = A.size();
        auto get = [&](int i, int k) {
            int lo = -1, hi = i + 1;
            while(hi - lo > 1) {
                int mid = (lo + hi) / 2;
                if(A[mid] > k)
                    hi = mid;
                else
                    lo = mid;
            }
            return lo;
        };
        int64_t ans = 0;
        for(int i = 0 ; i < N ; ++i) {
            for(int j = i - 1 ; j >= 0 ; --j) {
                if((A[j] & A[i]) == A[j])   break;
                A[j] &= A[i];
            }
            ans += get(i, k) - get(i, k - 1);
        }
        return ans;
    }
};
