class Solution {
public:
    #define lli long long int
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        lli ans = 0;

        for(int i=0;i<n;i++) 
           { if (nums[i]==k)
            ans++;
           }

        map<lli,lli> mpp1;

        for (int i = 0; i< n; i++) {
              int el = nums[i];
            map<lli, lli> mpp2;

             for (auto it: mpp1) {
               long long result = it.first & el;      
                mpp2[result] += it.second;

           if (result == k) 
           ans += it.second;

            }


            mpp2[el]++;

            mpp1=mpp2;
        }

        return ans;
    }
};
