class Solution {
    public long countSubarrays(int[] nums, int k) {
        int len = nums.length;
        long res = 0L;
        HashMap<Long, Long> map1 = new HashMap<>();
        for(int i=0; i<len; ++i) {
            HashMap<Long, Long> map2 = new HashMap<>();
            if(nums[i] == k) {
               ++res; 
            }
            map2.put((long)nums[i], 1L);
            for(long x : map1.keySet()) {
                long and = x & nums[i];
                if(and == k) {
                    res += map1.get(x);
                }
                map2.put(and, map2.getOrDefault(and, 0L)+map1.get(x));
            }
            map1 = map2;
        }
        return res;
    }
}
