class Solution {
public:
long long countSubarrays(vector<int>& nums, int k) {
   unordered_map<long long,long long> mp;
   long long ans=0;

   for(auto it: nums){
      unordered_map<long long,long long> tmp;
      for(auto ele: mp){
         tmp[(ele.first &it)] += ele.second;
      }
      tmp[it]++;

       mp.clear();
      for(auto i: tmp){
         if(i.first==k) ans+= i.second;   
         mp[i.first] = i.second;
      }
   }        

   return ans;
}
};

