class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {

        long long ans = 0;

        map<int,int> mp;

        for(auto& ele : nums)
        {
            map<int,int> newMp;

            for(auto& x : mp)
            {
                int val = x.first & ele;
                newMp[val] += x.second;
            }

            ++newMp[ele];

            ans += newMp[k];

            mp = newMp;
        }

        return ans;
    }
};
