#define ll long long
class Solution {
public:
    ll solve(vector<int>&v,int tar){
     unordered_map<ll, ll>mp1,mp2;
    ll size=v.size();
    ll one=0,count=0; 
        ll n=v.size();

    for (int i = 0; i < n; i++) {
        ll temp = v[i];
       ll equal = (temp^tar);
       // cout<<same<<" "<<ans<<"\n";
        count+=equal==0; 
        mp2[temp]++;

        auto ut = mp1.begin(); 
        while(ut!=mp1.end()){
           // cout<<res<<" "<<((it->first&b)^k)<<" "<<(it->first&b) << "\n" ;
            // if((ut->first&temp)^tar==0){
            //     res+=ut->second;
            // }

            one+=((ut->first&temp)^tar)==0?ut->second:0;
            mp2[(ut->first&temp)]+=ut->second;
            ut++;
        }
        mp1=mp2;
        mp2.clear();
    }
    one+=count;
    return one;
    }
    long long countSubarrays(vector<int>& a, int k) {
    return solve(a,k);
    }
};
