class Solution {
public:
    long long countSubarrays(vector<int>& arr, int k) {
        int n = arr.size();
        unordered_map<long long, long long> m;
         long long ans = 0;
        for (int i = 0; i < n; i++) {
            unordered_map<long long, long long> mp; 
            if (arr[i] == k)
                ans++;
            mp[arr[i]] = 1;
            for (auto& ii : m) {
                long long x = ii.first & arr[i]; 
                if (x == k) {
                    ans += ii.second;
                }
                mp[x]+=ii.second;
            }
            m = mp;
        }
        return ans;
    }
};
