class Solution {
    public long countSubarrays(int[] nums, int k) {
        int n = nums.length;
        long count = 0;

        HashMap<Long, Long> prevResults = new HashMap<>();

        for (int i = 0; i < n; ++i) {
            HashMap<Long, Long> currResults = new HashMap<>();

            if (nums[i] == k) {
                ++count;
            }
            currResults.put((long) nums[i], 1L);

            for (HashMap.Entry<Long, Long> entry : prevResults.entrySet()) {
                long val = entry.getKey();
                long freq = entry.getValue();

                long newAndResult = val & nums[i];
                if (newAndResult == k) {
                    count += freq;
                }

                currResults.put(newAndResult, currResults.getOrDefault(newAndResult, 0L) + freq);
            }

            prevResults = currResults;
        }

        return count;
    }
}
