class Solution {
public:

    long long secret(vector<int>&nums,int k ){
        int n = nums.size();
        unordered_map<long long, long long> amp;
        long long ans = 0;

        for (long long i = 0; i < n; ++i) {
            unordered_map<long long, long long> nmp;
            for (auto &e : amp) {
                long long newAnd = e.first & nums[i];
                nmp[newAnd] += e.second;
            }
            nmp[nums[i]]++;
            amp = nmp;

            if (amp.find(k) != amp.end()) {
                ans += amp[k];
            }
        }

        return ans;
    }
    long long countSubarrays(vector<int>& nums, int k) {
        return secret(nums,k);
    }
};
