class Solution {
public:
    long long countSubarrays(vector<int>& num, int k) {
        long long ans = 0;
        int n = num.size();
        vector<vector<int>> a(32,vector<int>(n+1)), r(32,vector<int>(n+1));
        for(int i=0;i<32;i++){
            for(int j=1;j<=n;j++){
                a[i][j] = num[j-1] >> i & 1; 
            }
        }
        for(int i=0;i<32;i++){
            r[i][n] = (a[i][n] == 0? n : n + 1);
            for(int j=n-1;j>=1;j--){
                if(a[i][j] == 0) r[i][j] = j;
                else r[i][j] = r[i][j+1];
            }
        }
        for(int i=1;i<=n;i++){
            int l = 1, rr = n;
            for(int j=0;j<32;j++){
                if(k >> j & 1){
                    rr = min(rr,r[j][i] - 1);
                }
                else{
                    l = max(r[j][i],l);
                }
            }
            if(rr >= l) ans += (rr - l + 1);
        }
        return ans;
    }
};
