class Solution {
public:
    int x(vector<int>& nums, int k)
    {
        int n = nums.size();

        long long ans = 0;

        unordered_map<long long, long long> c;

        for (int i = 0; i < n; ++i) {
            unordered_map<long long, long long> cr; 
            cr[nums[i]] = 1;

            for (auto& [v, f] : c) {
                long long na = v & nums[i];

                cr[na] += f;

                if (na == k) 
                    ans += f;

            }

            c = cr;

            if (nums[i] == k)
                ans++;
        }

        return ans;
    }
