class Solution {
public:

    long long countSubarrays(vector<int>& a, int k) {
        int n=a.size();
        long long ans=0;
        int t[100];
        int s[100];
        int top=0;
        for(int i=1;i<=n;++i)
        {
            ++top;
            t[top]=a[i-1];
            s[top]=1;
            for(int j=1;j<=top;++j) t[j]=t[j]&a[i-1];
            int ntp=0;
            for(int j=1;j<=top;++j)
            {
                if(j==1||t[j]!=t[ntp])
                {
                    ++ntp;
                    t[ntp]=t[j];
                    s[ntp]=s[j];
                }
                else    s[ntp]+=s[j];
            }
            top=ntp;
            for(int j=1;j<=top;++j)
            {
                if(t[j]==k) ans+=s[j];
                //cout<<"- "<<i<<" "<<j<<" "<<t[j]<<" "<<k<<" "<<s[j]<<" "<<ans<<endl;
            }
            top=min(top,50);
        }
        return ans;
    }
};
