class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:

        n = len(nums)

        c = 0 

        prev_res = defaultdict(int)

        for i in range(n):
            curr_res = defaultdict(int)

            if nums[i] == k:
                c += 1 

            curr_res[nums[i]] = 1 

            for key,val in prev_res.items():
                new_res = key & nums[i]

                if new_res == k:
                    c += val 

                curr_res[new_res] += val 
            prev_res = curr_res 

        return c
