class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:

        d = defaultdict(int)
        ans = 0
        print("________________________-")
        for i in range(len(nums)):
            if nums[i] == k:
                ans += 1
            new = defaultdict(int)

            for j in d.keys():
                if nums[i] & j == k:
                    ans += d[j]
                new[nums[i] & j] += d[j]
            d = new.copy()
            d[nums[i]] += 1

        return ans
