class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n=nums.size();
         long long ans=0;
        map<long long ,long long>mp;
        for(int i=0;i<n;i++){
//              if (nums[i] == k) ans++;
//         temp[nums[i]] = 1;

//         for (auto it : mp) {
//             long long lat = it.first & nums[i];
//             if (lat == k) ans += it.second;
//             temp[lat] += it.second;

            if(nums[i]==k)ans++;
             map<long long,long long>temp;
            temp[nums[i]]=1;
            for(auto it:mp){
                long long lat=it.first & nums[i];
                temp[lat]+=it.second;
                if(lat==k)ans+=it.second;

            }
            mp=temp;
        }
        return ans;
    }
};
