class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        l = len(nums)
        cnt = 0
        d = {}

        for i in range(l):
            d2 = {}

            if nums[i] == k:
                cnt += 1
            d2[nums[i]] = 1

            for val, freq in d.items():
                cur = val & nums[i]
                if cur == k:
                    cnt += freq
                d2[cur] = d2.get(cur, 0) + freq

            d = d2

        return cnt
