class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        count = 0
        n = len(nums)
        curr = defaultdict(int)
        for i in range(n):
            new_curr = defaultdict(int)
            for val in curr:
                new_val = val & nums[i]
                new_curr[new_val] += curr[val]
            new_curr[nums[i]] += 1
            if k in new_curr:
                count += new_curr[k]
            curr = new_curr
        return count
