class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        vector<vector<int>>p(32, vector<int>(nums.size()+1));
        for(int i=0;i<nums.size();++i){
            for(int j=0;j<32;++j){
                if(nums[i] & (1<<j)){
                    p[j][i+1] = 1 + p[j][i];
                }
            }
        }
        long long ans = 0;
        for(int i=0;i<nums.size();++i){
            int l = 0, r = i, f1=-1,f2=-1;
            while(l <= r){
                int m = l + (r-l)/2;
                int av = 0;
                for(int j=0;j<32;++j){
                    if (p[j][i+1] - p[j][m] == i-m+1){
                        av |= 1<<j;
                    }
                }
                if(av == k){
                    f1 = m;
                    r = m-1;
                } else if (av < k){
                    l = m+1;
                } else {
                    r = m - 1;
                }
            }
            l = 0, r = i;
            while(l <= r){
                int m = l + (r-l)/2;
                int av = 0;
                for(int j=0;j<32;++j){
                    if (p[j][i+1] - p[j][m] == i-m+1){
                        av |= 1<<j;
                    }
                }
                if(av == k){
                    f2=m;
                    l = m+1;
                } else if (av < k){
                    l = m+1;
                } else {
                    r = m - 1;
                }
            }
            if(f1 == -1){
                continue;
            } else {
                ans += f2 - f1 + 1;
            }
        }
        return ans;
    }
};
