#include <vector>
#include <unordered_map>
using namespace std;

class Solution {
public:
    long long countSubarrays(vector<int>& a, int k) {
        int n = a.size();
        long long cnt = 0;
        unordered_map<long long, long long> prev;

        for (int i = 0; i < n; i++) {
            unordered_map<long long, long long> cur;
            if (a[i] == k) cnt++;
            cur[a[i]] = 1;

            for (auto it : prev) {
                long long temp = it.first & a[i];
                if (temp == k) cnt += it.second;
                cur[temp] += it.second;
            }

            prev = cur;
        }

        return cnt;
    }
};
