class Solution {
    public:
        long long countSubarrays(vector<int>& myarray, int chomu) {
        long long mtlen = myarray.size();
        long long kaleen = 0;
        unordered_map<int, int> devMap;
        for (int i = 0; i < mtlen; ++i) {
            unordered_map<int, int> meanMap;
            meanMap[myarray[i]]++;
            for (auto& pair : devMap) {
                int ccand = pair.first & myarray[i];
                meanMap[ccand] += pair.second;
            }
            devMap = meanMap;
            kaleen += devMap[chomu];
        }
        return kaleen;

    }
};
