class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n=nums.size(),ptr=0;
        long long res=0;
        while(ptr<n){
            if((nums[ptr]&k)==k){
                int l=ptr,cnt=0,pre=-1;
                map<int,long long>dp;
                dp[nums[ptr]]=1;
                res+=dp[k];
                ptr++;
                while(ptr<n&&((nums[ptr]&k)==k)){
                    map<int,long long>ndp;
                    for(auto &[num,cnt]:dp){
                        ndp[num&(nums[ptr])]+=cnt;
                    }
                    ndp[nums[ptr]]+=1;
                    ptr++;
                    dp=ndp;
                    res+=dp[k];
                }
            }
            ptr++;
        }
        return res;
    }
};
