class Solution {
public:
    vector<vector<int>>PrefixandGenerator(const vector<int>&vec)
    {
        vector<vector<int>>ans(1,vector<int>(32,0));
        for(auto i:vec)
        {
            ans.emplace_back(ans[ans.size()-1]);
            int pos=0;
            while(i)
            {
                ans[ans.size()-1][pos]+=i&1;
                pos++;
                i>>=1;
            }
        }
        return ans;
    }
    int andsum(const vector<vector<int>>&andOrPrefix,int l,int r)
    {
        int ans=0,curr=1;
        for(int i=0;i<andOrPrefix[0].size();i++)
        {
            if(andOrPrefix[r+1][i]-andOrPrefix[l][i]==r-l+1)
            {
                ans+=curr;
            }
            curr<<=1;
        }
        return ans;
    }
    long long atleast(vector<int>& nums,int k){
        long long ans=0;
        int n = nums.size();
        int i=0,j=0;
        vector<vector<int>> andprefixval = PrefixandGenerator(nums);
        int andvalue=0;
        while(j<n){
            andvalue = andsum(andprefixval,i,j);
            if(andvalue >= k){
                ans += (j-i+1);
                j++;
            }
            else{
                if(i==j){
                    i++,j++;
                }
                else{
                    i++;
                }
            }

        }
        return ans;

    }
    long long countSubarrays(vector<int>& nums, int k) {
        return atleast(nums,k)-atleast(nums,k+1);
    }
};
