class Solution {
public:

    typedef long long int ll;

    long long countSubarrays(vector<int>& nums, int k) {
        map<ll,ll>mp;
        ll res = 0;
        for(auto num:nums){
            map<ll,ll>newMp;
            newMp[num]++;
            for(auto it:mp){
                newMp[it.first&num] += it.second;
            }
                // newSt.insert(it&num);

            for(auto it:newMp){
                if(it.first==k) res+=it.second;
            }

            mp=newMp;
        }
        return res;
    }
};
