class Solution {

public:

    long long func(vector<int>& n, int k) {

        int s = n.size();

        long long c = 0;



        unordered_map<long long, long long> p;



        for (int i = 0; i < s; ++i) {

            unordered_map<long long, long long> q; 

            if (n[i] == k) {

                ++c;

            }

            q[n[i]] = 1;

            for (auto& [v, f] : p) {

                long long r = v & n[i];

                if (r == k) {

                    c += f;

                }

                q[r] += f;

            }



            p = q;

        }



        return c;

    }


    long long countSubarrays(vector<int>& nums, int k) {

         return func(nums,  k);

    }
};
