class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) 
    {

        int n = nums.size();
        long long ans  = 0;

        map<int, int> mp;

        for (int i = 0; i < n; i++) 
        {
            map<int, int> mppp;
            for (auto &it : mp) 
            {
                int newValue = it.first & nums[i];
                mppp[newValue] += it.second;
            }

            mppp[nums[i]]++;

            mp = mppp;

            if (mp.find(k) != mp.end()) 
            {
                ans += mp[k];
            }
        }

        return ans;



    }
};
