class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {

        int n=nums.size();

        unordered_map<long long, long long> pre;
        long long cnt=0;

        for(int i=0; i<n; i++)
        {
            if(nums[i]==k)
            {
                cnt++;
            }

            unordered_map<long long, long long> cur;
            cur[nums[i]]++;

            for(auto it : pre)
            {
                long long temp=nums[i]&it.first;

                if(temp==k)
                {
                    cnt+=it.second;
                }

                cur[temp]+=it.second;
            }

            pre=cur;
        }

        return cnt;
    }
};
