class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        vector<pair<int,int>>ands;
        int n = nums.size();
        long long ans = 0;


        for(int i = 0; i < n; i++){
            ands.emplace_back(nums[i], 1);
            vector<pair<int, int>>t;
            if((nums[i] & k) == k)
            for(int j = 0; j < ands.size(); j++){
                ands[j].first &= nums[i];
                if((ands[j].first & k) != k)continue;

                if(ands[j].first == k)ans += ands[j].second;

                if(!t.empty() && t.back().first == ands[j].first)
                    t.back().second += ands[j].second;
                else
                t.emplace_back(ands[j].first, ands[j].second);
            }
            ands.swap(t);
        }

        return ans;
    }
};
