class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long ans = 0; 
        unordered_map<long long, long long> m; 

        int t=0;

        while ( t < nums.size()) { 
            int element=nums[t];
            if (element == k)  
                ans++; 
            unordered_map<long long, long long> c;  
            c[element] = 1; 

            for (auto it=m.begin(); it!=m.end(); it++) { 
                long long And = it->first & element; 
                if (And == k)
                    ans = ans+it->second; 
                c[And] += it->second; 
            } 
            m = c; 
            t++;
        } 
        return ans; 
    }
};
