public class Solution {
    public long CountSubarrays(int[] nums, int k) {
        double ans = 0; 
        Dictionary<double, double> oneback = new Dictionary<double, double>(); 
        long kk = k;
        int i = 0;
        while (i < nums.Length) { 
            Dictionary<double, double> curr = new Dictionary<double, double>(); 

            if (nums[i] == k) { 
                ++ans; 
            } 
            curr[nums[i]] = 1; 

            foreach (var pair in oneback.ToList()) {
                long val = (long)pair.Key;
                long freq = (long)pair.Value;
                long newAND = (long)val & nums[i];

                if (!(newAND == kk)) {}
                else ans += freq;

                if (curr.ContainsKey(newAND)){}
                else curr[newAND] = 0;

                curr[newAND] += freq;
            }
            oneback = curr;
            ++i;
        } 

        return (long)ans;
    }
}
