class Solution {
public:
    long long countSubarrays(vector<int>& a, int k) {
        long long ans =0;
        int n = a.size();
        map<int,int> mp;
        for(int x:a)
        {
            map<int,int> np;
            if(x==k)
            {
                ans++;
            }
            np[x] = np[x]+1;
            for(auto &en: mp)
            {
                int andd = (en.first & x);
                if(andd ==k)
                {
                    ans =ans + en.second;
                }
                np[andd] = np[andd] + en.second;
            }
            mp = np;
        }
        return ans;
    }
};
