#include <vector>
#include <map>

using namespace std;

class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        map<int, int> freq; 
        long long cnt = 0;

        for (int i = nums.size() - 1; i >= 0; --i) {
            map<int, int> newfreq; 
            if (nums[i] == k) {
                cnt++;
            }
            for (const auto& p : freq) {
                int and_result = nums[i] & p.first;
                if (and_result == k) {
                    cnt += p.second;
                }
                newfreq[and_result] += p.second;
            }
            newfreq[nums[i]]++;
            freq = newfreq;
        }

        return cnt;
    }
};
