class Solution {
public:
    pair <long long , map <long long,long long> > check(int num , map <long long,long long> &mp , int k)
    {
        map <long long,long long> ans ;
        long long cnt = num == k ;
        ans[num] = 1 ;
        for(auto it : mp)
        {
            long long And = num&it.first ;
            if(And == k) cnt += it.second ;
            ans[And] += it.second ;
        }
        return {cnt,ans} ;
    }
    long long countSubarrays(vector<int>& nums, int k) 
    {
        map <long long , long long> mp ;
        long long ans = 0 , n = nums.size() ;    
        for(auto it : nums)
        {
            auto temp = check(it,mp,k) ;
            mp = temp.second ;
            ans += temp.first ;
        }
        return ans ;
    }
};
