class Solution {
public:
    long long countSubarrays(vector<int>& array, int target) {

        long long tl = 0;
        unordered_map<long long, long long> prevres;
        for (int idx = 0; idx < array.size(); ++idx) {
            unordered_map<long long, long long> currRes; 
            if (array[idx] == target) {
                ++tl;
            }
            currRes[array[idx]] = 1;
            for (auto& [value, freq] : prevres) {
                long long newAndResult = value & array[idx];
                if (newAndResult == target) {
                    tl += freq;
                }
                currRes[newAndResult] += freq;
            }

            prevres = currRes;
        }
        return tl;
    }
};
