class Solution { 
public: 
    long long countSubarrays(vector<int>& nums, int k) { 
        int n = nums.size(); 

        unordered_map<long long, long long> mp; 

        long long cnt = 0; 

        for (int i = 0; i < n; ++i) { 
            unordered_map<long long, long long> ump;  

            if (nums[i] == k) { 
                cnt++; 
            } 
            ump[nums[i]] = 1; 

            for (auto it : mp) { 
                int a=it.first;
                int b = it.second;
                if ((a & nums[i]) == k) { 
                    cnt += b; 
                } 
                ump[a & nums[i]] += b; 
            } 

            mp = ump; 
        } 

        return cnt; 
    } 
};
