class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        # a & b
        @cache
        def runner(btw, idx):
            if idx >= len(nums):
                return 0

            tmp = btw & nums[idx]

            if tmp == k:
                return 1 + runner(tmp, idx + 1)

            return runner(tmp, idx + 1)


        res = 0

        for idx, val in enumerate(nums):
            res = res + runner(val, idx)

        return res
